contactTypes = data['availableContactTypes'];
values = Promise.resolve(contactTypes);


if (contactTypes.includes('Submitter Identification') && contactTypes.includes('Manufacturer of Imported Products')) {
  const found = data['contactTypes'].find((o) => {
  if (o.submitterIdentification) {
    return o.submitterIdentification.submitterRole === 'createsTobaccoProducts'
  }
  });

  if (found) {
    let newContactTypes = contactTypes.slice();
    const idx = newContactTypes.indexOf('Manufacturer of Imported Products');
    if (idx !== -1) {
      newContactTypes.splice(idx, 1);
    }

    values = Promise.resolve(newContactTypes);
  }
}

